// AGGREGATE ROOT
@AggregateRoot
public class Cuenta {
    private CuentaId id;
    private Saldo saldo;
    private List<Movimiento> movimientos;

    public DomainEvent debitar(Monto monto) {
        if (saldo.menorQue(monto)) throw new SaldoInsuficienteException();
        saldo = saldo.restar(monto);
        return new CuentaDebitada(id, monto, Instant.now());
    }
}

// DOMAIN EVENT
public record CuentaDebitada(CuentaId id, Monto monto, Instant cuando) {}

