@CircuitBreaker(name = "pagos", fallbackMethod = "fallback")
@Retry(name = "pagos")
@TimeLimiter(name = "pagos")
@Bulkhead(name = "pagos")
public Mono<Response> pagar(Request req) {
    return webClient.post().bodyValue(req).retrieve().bodyToMono(Response.class);
}

public Mono<Response> fallback(Request req, Throwable t) {
    return Mono.just(Response.pendiente("Reintentando..."));
}

